#!/usr/bin/env ruby

def unique p
  pc = nil
  sl = 0
  ll = {}
  p.chars.each do |c|
    if pc != nil and (pc == 'z' and c == 'a' or pc.succ == c)
      sl += 1
    else
      sl = 1
    end
    ll[c] ||= 1
    ll[c] = [ll[c], sl].max
    pc = c
  end
  return ll.to_a.reduce(0) { |s,v| s+v[1] }
end

[ ['', 0],
  ['a', 1],
  ['cac', 2],
  ['zab', 6],
  ['cofzxdlhnf', 9],
  ["", 13871],
  ["", 259259]
].each do |p, exp|
  act = unique p
  if act == exp
    print 'PASS'
  else
    print 'FAIL'
  end
  if p.length > 30
    puts " unique '#{p[0..30]}...' = #{act} (#{exp})"
  else
    puts " unique '#{p}' = #{act} (#{exp})"
  end
end
